function addNewRowOnCellClick(flag) {
  if (flag == 'leftTable') {
    var tabGrid = document.getElementById('leftMainTabletBody');
  }

  if (flag == 'rightTable') {
    var tabGrid = document.getElementById('rightMainTabletBody');
  }

  var rowCnt = tabGrid.rows.length; // table row count.

  var tr = tabGrid.insertRow(rowCnt); // the table row. 

  tr.setAttribute("style", "height:20px;border: 1px solid gray;");

  for (var c = 0; c < 5; c++) {
    var td = document.createElement('td'); // table definition.

    td = tr.insertCell(c);
    td.setAttribute("contenteditable", "true");

    if (c == 4) {
      td.innerText = (parseInt(rowCnt.toString()) + 1).toString();
    }

    if (c == 0 || c == 4) {
      td.setAttribute("contenteditable", "false");
    }

    td.setAttribute("style", "border: 1px solid gray; "); // td.setAttribute("class", "ng-tns-c56-0")

    td.setAttribute('onclick', '');

    td.onclick = function () {
      if ($(this).closest("tr").is(":last-child")) {
        addNewRowOnCellClick(flag);
      }
    };
  }
}
