      td.setAttribute("contenteditable", "false");
    }

    td.setAttribute("style", "border: 1px solid gray; "); // td.setAttribute("class", "ng-tns-c56-0")

    td.setAttribute('onclick', '');

    td.onclick = function () {
      if ($(this).closest("tr").is(":last-child")) {
        addNewRowOnCellClick(flag);
      }
    };
  }
}
